async detectBatteryType() {
  const voltage = await this.readBatteryVoltage();

  if (voltage >= 2800 && voltage <= 3200) {
    return 'CR2032'; // 3V button cell
  } else if (voltage >= 1300 && voltage <= 1600) {
    return 'AA'; // 1.5V
  } else if (voltage >= 1200 && voltage <= 1500) {
    return 'AAA'; // 1.5V
  }

  return 'UNKNOWN';
}
